package com.springbook.biz.board.impl;import com.springbook.biz.board.BoardService;import com.springbook.biz.board.BoardVo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Collections;import java.util.List;@Service("BoardService")public class BoardServiceImpl implements BoardService {    @Autowired    private BoardDAOSpring boardDAO;    @Override    public void insertBoard(BoardVo vo) {        boardDAO.insertBoard(vo);    }    @Override    public void updateBoard(BoardVo vo) {        boardDAO.updateBoard(vo);    }    @Override    public void deleteBoard(BoardVo vo) {        boardDAO.deleteBoard(vo);    }    @Override    public BoardVo getBoard(BoardVo vo) {        return boardDAO.getBoard(vo);    }    @Override    public List<BoardVo> getBoardList(BoardVo vo) {        return boardDAO.getBoardList(vo);    }}