package com.springbook.biz.board.impl;import com.springbook.biz.board.BoardService;import com.springbook.biz.board.BoardVo;import java.util.Collections;import java.util.List;public class BoardServiceImpl implements BoardService {    private BoardDAO boardDAO;    public void setBoardDAO(BoardDAO boardDAO) {        this.boardDAO = boardDAO;    }    @Override    public void insertBoard(BoardVo vo) {        boardDAO.insertBoard(vo);    }    @Override    public void updateBoard(BoardVo vo) {        boardDAO.updateBoard(vo);    }    @Override    public void deleteBoard(BoardVo vo) {        boardDAO.deleteBoard(vo);    }    @Override    public BoardVo getBoard(BoardVo vo) {        return boardDAO.getBoard(vo);    }    @Override    public List<BoardVo> getBoardList(BoardVo vo) {        return boardDAO.getBoardList(vo);    }}