package com.springbook.biz.user.impl;import com.springbook.biz.common.JDBCUtil;import com.springbook.biz.user.UserVO;import org.springframework.stereotype.Repository;import java.sql.Connection;import java.sql.DriverManager;import java.sql.PreparedStatement;import java.sql.ResultSet;@Repository("userDAO")public class UserDAO {    private Connection        conn = null;    private PreparedStatement stmt = null;    private ResultSet         rs   = null;    private final String USER_GET = "select * from users where id=?";    public UserVO getUser(UserVO userVO) {        UserVO user = null;        try {            String url = "jdbc:oracle: this:@localhost: 1521: xe";            conn = DriverManager.getConnection (url, "hr", "hr") ;            stmt = conn.prepareStatement (USER_GET) ;            stmt.setString(1, userVO.getId());            if (rs.next()) {                user = new UserVO();                user.setId(rs.getString("ID"));                user.setPassword(rs.getString("PASSWORD"));                user.setName(rs.getString("NAME"));                user.setRole(rs.getString("ROLE"));            }        } catch (Exception e) {            e.printStackTrace();        } finally {            JDBCUtil.close(stmt, conn);        }        return user;    }}