package org.example.sprinttestjs;import org.springframework.context.support.AbstractApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;import org.springframework.context.support.GenericXmlApplicationContext;import polymorphism.CollectionBean;import java.util.List;public class CollectionBeanClient {    public static void main(String[] args) {        AbstractApplicationContext factory = new GenericXmlApplicationContext("applicationContext.xml");        CollectionBean bean = (CollectionBean) factory.getBean("collectionBean");        List<String> addressList = bean.getAddressList();        for (String address : addressList) {            System.out.println(address.toString());        }        factory.close();    }}